import axios from 'axios';
import { useEffect, useState } from 'react';
import './App.css';

function App() {

  const [id, setid] = useState();
  const [descricao, setdescricao] = useState();
  const [listaTarefa, setListaTarefa] = useState([]);

  useEffect(() => {
      buscar();
  }, []);

  function buscar(){
    axios.get('http://localhost:3100/tarefa').then(resultado => {
      console.log(resultado.data);
      setListaTarefa(resultado.data);
  })
  }

  function salvar(event){
    event.preventDefault();
    let tarefa = {
      id: id,
      descricao: descricao
    };
    console.log('tarefa', tarefa);

    axios.put('http://localhost:3100/tarefa', tarefa).then(() => {
      buscar();
    })
  }

  function editar(event){
    event.preventDefault();
    let tarefa = {
      id: id,
      descricao: descricao
    };
    console.log('tarefa', tarefa);

    axios.put('http://localhost:3100/tarefa', tarefa).then(() => {
      buscar();
    })
  }

  function excluir(event){
    event.preventDefault();
    let tarefa = {
      id: id,
      descricao: descricao
    };
    console.log('tarefa', tarefa);

    axios.delete('http://localhost:3100/tarefa', tarefa).then(() => {
      buscar();
    })
  }

  return (
    <div className="container">

    <form onSubmit={(event) => salvar (event)} >
      <div className="mb-3">
        <label className="form-label">Descrição</label>
        <input type="text" className="form-control" value={descricao} onChange={(event) => setdescricao(event.target.value)} />
      </div>

      <button type="submit" className='btn btn-primary'>Salvar</button>
      <button type="submit" className='btn btn-primary'>Editar</button>
      <button type="submit" className='btn btn-primary'>Excluir</button>
    </form>

      <h3>Lista de Tarefas</h3>

      <table className="table">
        <thead>
          <tr>
            <td>Tarefa</td>
          </tr>
        </thead>

      </table>

    </div>
  );
}

export default App;
