import axios from 'axios';
import { useEffect, useState } from 'react';
import './App.css';

function App() {

  const [id, setid] = useState();
  const [descricao, setdescricao] = useState();
  const [listaTarefa, setListaTarefa] = useState([]);

  useEffect(() => {
      buscar();
  }, []);

  function buscar(){
    axios.get('http://localhost:3100/tarefa').then(resultado => {
      console.log(resultado.data);
      setListaTarefa(resultado.data);
  })
  }

  function salvar(event){
    event.preventDefault();
    let tarefa = {
      id: id,
      descricao: descricao
    };
    console.log('tarefa', tarefa);

    axios.put('http://localhost:3100/tarefa', tarefa).then(() => {
      buscar();
    })
  }

  function editar(event){
    event.preventDefault();
    let tarefa = {
      id: id,
      descricao: descricao
    };
    console.log('tarefa', tarefa);

    axios.put('http://localhost:3100/tarefa', tarefa).then(() => {
      buscar();
    })
  }

  function excluir(event){
    event.preventDefault();
    let tarefa = {
      id: id,
      descricao: descricao
    };
    console.log('tarefa', tarefa);

    axios.delete('http://localhost:3100/tarefa', tarefa).then(() => {
      buscar();
    })
  }

  return (
    <div className="container">

    <form onSubmit={(event) => salvar (event)} >
      <h1>Lista de Tarefas</h1>
      <div className="mb-3">
        <input type="text" className="form-control" placeholder="Digite sua tarefa..." value={descricao} onChange={(event) => setdescricao(event.target.value)} />
      </div>
      <button type="submit" className='btn btn-primary'>Adicionar</button>
    </form>
    <br/>

      <table className="table">
        <thead>
          <tr>
            <td><b>Tarefas Adicionadas:</b></td>
            <td> </td>
          </tr>
        </thead>
        <tbody>
          {
            listaTarefa.map((tarefa, index) => (
              <tr key={index}>
                <td>{tarefa.descricao}</td>
                <td></td>
              </tr>
            ))
          }
        </tbody>
      </table>

    </div>
  );
}

export default App;
